mapper.select:
select
guid,
message,
add_visitor,
add_visitor_id,
add_time
from message
where delete_flag = 0

   and guid = #{ds.guid}


    and add_visitor = #{ds.addVisitor}


    and message like concat('%',#{ds.message},'%')


    and add_visitor_id=#{ds.addVisitorId}


    and add_time between #{ds.addTime} and #{ds.addTime}

select count(guid)
from message
where delete_flag = 0
and add_visitor_id = #{ds.addVisitorId}
and add_time = #{ds.addTime}
